mapper.select:
SELECT

FROM proj_info
LEFT JOIN district on district.district_id=proj_info.district_id
LEFT JOIN area on area.area_id=proj_info.area_id
LEFT JOIN
(select newcode as inewcode,count(*) as imgcount from proj_image group by newcode)
i  on proj_info.newcode = i.inewcode
WHERE 1=1

  AND  newcode = #{newcode}


  AND n_or_e = #{projFlag}


  AND proj_info.city_id = #{cityId}


  AND proj_info.city_id = #{currentCityId}


  AND proj_info.district_id = #{districtId}


  AND proj_info.area_id = #{areaId}


  AND proj_info.is_activity = #{isActivity}


  AND  projname LIKE CONCAT('%', #{keyword}, '%')


  AND ( projname LIKE CONCAT('%', #{projName}, '%')
  OR  nickname LIKE CONCAT('%', #{projName}, '%'))


  AND is_approve = #{isApprove}


  AND is_del = #{isDel}


 AND imgcount ISNULL


  AND imgcount > 0

ORDER BY update_time DESC

mapper.update:
UPDATE proj_info

  is_del = 1,
  deldate = now(),

    delreason = #{delreason,jdbcType=VARCHAR},


    updater_id = #{updaterId,jdbcType=INTEGER}


WHERE newcode = #{newcode,jdbcType=INTEGER}

mapper.select:
SELECT newcode, projname
FROM proj_info
WHERE projname=#{projname}
AND is_del=0 AND n_or_e=1 AND is_approve=1 LIMIT 1;

mapper.update:
update proj_info


    is_activity = #{isActivity,jdbcType=INTEGER},

      build_tags = array_append(build_tags, 18),


      build_tags = array_remove(build_tags, 18),



    newcode = #{newcode,jdbcType=INTEGER}


WHERE newcode = #{newcode,jdbcType=INTEGER}
